import React, {Component} from 'react'
import logo from './logo-sk.png'
import './Login.css'

export default class Login extends Component {

    state = {
        isLoading,
        result : []
    };

    onLoginSubmit() {
        this.state.isLoading = true;
        fetch('http://localhost:8080/verify', {
        method: "POST",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            email: this.refs.email.value,
            password: this.refs.password.value
        })
        });
    }

    async componentDidMount() {
        const response = await fetch('/verify');
        const body = await response.json();
        this.setState({ result: body, isLoading: false });
    }

    renderResult() {
        return this.state.items.map((item) => {
            return (
                <div key={item}>
                    {item}
                </div>
            );
        });
    }

    render() {
        const {isLoading} = this.state;

        if (isLoading) {
            return <p>Loading...</p>;
        }
        return(
            <div class="login">
                <div class="login-triangle"></div>

                <p class="login-header"><strong>S</strong>kelters</p>

                <form class="login-container" onSubmit={this.onLoginSubmit.bind(this)}>
                    <p><input name="nom" type="text" placeholder="Email" ref="email"/></p>
                    <p><input name="password" type="password" placeholder="Password" ref="password"/></p>
                    <p><input type="submit" value="LOGIN"/></p>
                </form>
                <this.renderResult/>
            </div>
        );
    }
}
