import React, {Component} from 'react'
import logo from './logo-sk.png'
import './Login.css'

export default class Login extends Component {

    constructor() {
        super();
        this.state = {
            email: "",
            password: ""
        };
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChange(e) {
        this.setState({[e.target.name]:e.target.value})
    }

    onSubmit(e) {
        e.preventDefault()
        const user = {
            email: this.state.email,
            password: this.state.password
        };
        console.log(user);
    }

    render() {
        return(
            <div className="login">
                <div className="login-triangle"></div>

                <p className="login-header"><strong>S</strong>kelters</p>

                <form className="login-container" onSubmit={this.onSubmit}>
                    <p><input name="email" type="text" placeholder="Email" value={this.state.email} onChange={this.onChange}/></p>
                    <p><input name="password" type="password" placeholder="Password" value={this.state.password} onChange={this.onChange}/></p>
                    <p><input type="submit" value="LOGIN"/></p>
                </form>
            </div>
        );
    }
}
