import React, {Component} from 'react'
import logo from './logo-sk.png'
import './Login.css'

export default class Login extends Component {

    constructor() {
        super();
        this.state = {
            email: "",
            password: ""
        };
    }

    async onLoginSubmit() {
        const response = await fetch('/users');
        const body = await response.json();
        this.setState({result: body});
    }

    render() {
        return(
            <div className="login">
                <div className="login-triangle"></div>

                <p className="login-header"><strong>S</strong>kelters</p>

                <form className="login-container" onSubmit={this.onLoginSubmit.bind(this)}>
                    <p><input name="email" type="text" placeholder="Email" value={this.state.email}/></p>
                    <p><input name="password" type="password" placeholder="Password" value={this.state.password}/></p>
                    <p><input type="submit" value="LOGIN"/></p>
                </form>
            </div>
        );
    }
}
