import React, {Component} from 'react'
import logo from './logo-sk.png'
import './Login.css'

export default class Login extends Component {

    state = {
        result: ""
    };

    onLoginSubmit() {
        fetch('http://localhost:8080/verify', {
        method: "POST",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            email: this.refs.email.value,
            password: this.refs.password.value
        })
        }).then((data) => {
            this.setState({ result: data })                    
        })
          .catch((error) => {
            console.log(error, "catch the hoop")
        });
    }

    render() {
        return(
            <div className="login">
                <div className="login-triangle"></div>

                <p className="login-header"><strong>S</strong>kelters</p>

                <form className="login-container" onSubmit={this.onLoginSubmit.bind(this)}>
                    <p><input name="nom" type="text" placeholder="Email" ref="email"/></p>
                    <p><input name="password" type="password" placeholder="Password" ref="password"/></p>
                    <p><input type="submit" value="LOGIN"/></p>
                </form>
            </div>
        );
    }
}
